from PIL import Image
import os, sys
from PIL.ExifTags import TAGS
import ByteString



def get_exif(fn):
    ret = {}
    i = Image.open(fn)
    info = i._getexif()
    for tag, value in info.items():
        decoded = TAGS.get(tag, tag)
        ret[decoded] = value
    return ret

